import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {Link, useParams, useNavigate} from 'react-router-dom';
import '../App.css';

const Repos = ()=>{
    let {pagenum} = useParams();
    const [repoData, setRepoData] = useState(undefined);
    const [page, setPage] = useState(parseInt(pagenum));
    const [disable, setDisable] = useState(false);
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        setPage(parseInt(pagenum));
        setError(false);
        const fetchData = async () => {
            try{
                const { data } = await axios.get(`https://api.github.com/orgs/Netflix/repos?sort=stars?page=${page}`);
                console.log(data);
                setRepoData(data);
                setLoading(false);
            }catch(e){
                console.log(e);
                setError(true);
            }
            // try{
            //     const { data } = await axios.get(`http://localhost:4000/games/${page + 1}`);
            //     setDisable(false);
            // }catch(e){
            //     setDisable(true);
            // }
        }
        fetchData();
      }, [page, pagenum])


      if(error){
        return (
          <div>
            <h1>404 ERROR: NOT FOUND</h1>
          </div>
        )
      }

      if(loading){
        return (
          <div>
            <h1>Loading...</h1>
          </div>
        )
      }

      if(repoData){
        return (
            <h1>Hi</h1>
        )
      }
}

export default Repos;
