import React, { useState, useEffect } from "react";
import axios from "axios";
import {useParams, useNavigate } from "react-router-dom";
import "../App.css";

const GameDetails = (props) => {
    let {repoName} = useParams();
    let {pagenum} = useParams();
    const [commitData, setCommitData] = useState(undefined);
    const [page, setPage] = useState(parseInt(pagenum));
    const [disable, setDisable] = useState(false);
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

  useEffect(() => {
    setPage(parseInt(pagenum));
    setError(false);
    async function fetchData() {
      try {
        const { data } = await axios.get(`https://api.github.com/repos/netflix/${repoName}}/commits?page=${page}`);
        setCommitData(data);
      } catch (e) {
        setError(true);
        console.log(e);
      }
    }
    fetchData();
  }, [id, copies]);


  if (loading) {
    return (
      <div>
        <h2>Loading....</h2>
      </div>
    );
  } else if (error) {
    return (
      <div>
        <h2>404: NO DATA</h2>
      </div>
    );
  }

  return (
    <div>
      <div className="card mb-3">
        <div className="card-body">
          <img src={img} className="card-img-top img-thumbnail" alt="..." />
          <div className="card-body">
            <h5 className="card-title">
              {/* {gameData.name ? gameData.name : "No name Available"} */}
            </h5>
            <ul className="list-group list-group-flush">

            </ul>
          </div>
          <button
            type="button"
            className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 focus:outline-none"
            data-bs-toggle="modal"
            data-bs-target="#buyModal"
          >
            Buy Game
          </button>
          <button
            type="button"
            className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 focus:outline-none"
            data-bs-toggle="modal"
            data-bs-target="#sellModal"
          >
            Sell Game
          </button>
        </div>
      </div>
    </div>
  );
};
export default GameDetails;
